import React from 'react';
import DropdownBtn from './DropdownBtn';
import Screen from './Screen';
import Keypad from './Keypad';
import Dropdown from './Dropdown';

class App extends React.Component {
    state = {
        isDropdownOpen: false,
        display: "0",
        cleared: false,
        firstOperand: "",
        history: {}
    }

    calculate = () => {
        if (this.state.firstOperand === "") {
            this.setState({ firstOperand: this.display })
        } else {
        this.setState({ display: eval(this.state.firstOperand + this.state.display)})
        }
    }
    clear = () => {
        if (!this.state.cleared) {
        this.setState({ display: "0"})
        } else {
            this.setState({ firstOperand: "" })
            this.setState({ display: "0" })
            this.setState({ cleared: true })
        }
    }
    handleClick = (button) => {
        if (button === 'C') {
            this.clear();
        } else if (button === '=') {
            this.calculate();
        } else if(button === '/' || button === '*' || button === '-' || button === '+') {
            this.setState({ firstOperand: this.state.display })
            this.clear();
        } else if(this.state.display === '0') {
            this.setState({ display: button});
        } else if (this.state.display.length < 9){
            this.setState({ display: this.state.display + button});
        }
    }
    handleDropdownClick = () => {
        this.setState({ isDropdownOpen: true })
    }
    render() {
        return (
            <div className="container">
                <header>
                    <div className="notch" />
                    <DropdownBtn
                        handleDropdownClick={this.handleDropdownClick}
                        isDropdownOpen={this.state.isDropdownOpen} />
                </header>
                <Dropdown
                    handleDropdownClick={this.handleDropdownClick}
                    isDropdownOpen={this.state.isDropdownOpen} />
                <Screen
                    firstOperand={this.state.firstOperand}
                    display={this.state.display} />
                <Keypad
                    handleClick={this.handleClick} />
                <div className="home" />
            </div>
        )
    }
}

export default App;
